module.exports = {
  'step one' : function (browser) {
    browser
      .url('http://www.google.com/')
      .waitForElementVisible('body', 2500)
      .setValue('input[type=text]', 'white shepherds')
      .waitForElementVisible('button[name=btnG]', 2500)
  },

  'step two' : function (browser) {
    browser
      .click('button[name=btnG]')
      .pause(3500)
      .assert.containsText('#main', 'A White German Shepherd')
           .end();
  }
};